import './main.css'
import Api from '../api/Api'
import React, {useEffect, useState} from 'react';

const Main = () => {

    const [getIp, setIp] = useState();
    const [dados, setDados] = useState('')

    function pegaIp(){
        Api.get('/ip/').then((response) => {
            setIp(response.data)
        })
    }

    const onChange = event =>{
        localStorage.setItem('value', event.target.value);
        setDados(event.target.value)
    }

    const salvar= () =>{
        localStorage.getItem('value')
    }



   

    return (
        <div className="content2">
            <div class="container">
                <div class="col-12">
                  <label for="name">Nome</label>
                  <input type="text" id="name" value={dados} onChange={onChange}/>
                  <p>{dados}</p>
                </div>
                 <div class="col-6">
                    <label for="pro">Profiss√£o</label>
                    <input type="text" id="pro"/>
                 </div>
                <div class="col-6">
                     <label for="cel">Celular</label>
                    <input type="text" id="cel"/>
                </div>
                <div class="col">
                    <label for="ip">MEU IP</label>
                    <input  id="ip" value={getIp}/>
                 </div>
                <div class="col-auto align-end">
                    <button onClick={pegaIp}>Encontrar o meu IP</button>
                </div>

                <div class="col-bt align-self">
                    <button>SALVAR</button>
                    
                </div>
                <div class="col-bt align-self">
                    <button>LIMPAR</button>
                    
                </div>
            </div>
        </div>
     );
}
 
export default Main;
